DATETRUNC() --? Truncates the date to the specific part.

SELECT
OrderID,
CreationTime,
DATETRUNC(month, CreationTime) AS 'Month_dt',
DATETRUNC(day, CreationTime) AS 'Day_dt',
DATETRUNC(minute, CreationTime) AS 'Minute_dt'
FROM Sales.Orders;

